import React, {useState, useEffect, useRef} from 'react'

//this is customize bootstrap
import '../scss/all.css';

import '../jquery.js';
import '../bootstrap-5.2.1/dist/js/bootstrap.min.js';
import '../bootstrap-5.2.1/dist/css/bootstrap.min.css';

const Login=()=>{
    return(
        <div className="d-flex justify-content-center">
            <div className="d-flex mt-3">
                <form  className="alert alert-secondary " action=" http://localhost:8080/api/login" method="post">
                    <div className="input-group mb-3">
                        <span className="input-group-text">username</span>
                        <input type="text" class="form-control" name="username" placeholder="Username" aria-label="Username" />
                    </div>
                    <div className="input-group">
                        <span class="input-group-text">password</span>
                        <input className="form-control ms-3" type="password" name="password" placeholder="Enter Password" required/><br/>
                    </div>
                    <button className="btn btn-outline-secondary" type="submit">Login</button>
                </form>
            </div>
        </div>
    )
}

export default Login;
