import React, {useState, useEffect, useRef} from 'react'

//this bootstrap is prototype
//import '../bootstrap-5.2.1/dist/css/bootstrap.min.css';

//this is customize bootstrap
import '../scss/all.css';

import '../jquery.js';
import '../bootstrap-5.2.1/dist/js/bootstrap.min.js';

function Search(props){
    const [data,setData]=useState([{}]);
    const [readystate,setReadystate]=useState("");
    const mounted=useRef();
    const formRef = useRef();
    const [check,setCheck]=useState();
    const [value,setValue]=useState();
    const [img, setImg]=useState({});
    const [imgready, setImgready]=useState("unready");
    const [imagesrc,setImageSrc]=useState();

    let cnt=useRef();

    const handleOnChange = (event) => {
        let name=event.target.files[0].name;
        let time=String(event.target.files[0].lastModifiedDate);
        let datatype=event.target.files[0].type;

        console.log('Files: ', name);
        console.log('Time: ', time);
        console.log('Type: ', datatype);

        //obj={File:file, date: time, type:datatype};
        //console.log('Check: ',obj);
        //setImg({File: obj[File], Date: obj[Date], type: obj["type"]});

        setImg(previousState => {
            setImgready("ready");
            return { ...previousState, name:name, date:time, type:datatype, ok:"123"}
        });

        let file=event.target.files[0];
        const reader = new FileReader();

        reader.addEventListener("load", function () {
            // convert image file to base64 string
            setImageSrc(reader.result)
        }, false);

        if (file) {
            reader.readAsDataURL(file);
        }
    };


    const handleOnResetForm = () => {
        formRef.current.reset();
      };

    useEffect(()=>{
        if(!mounted.current){ //componentDidMount
            setReadystate("unready");
            mounted.current=true;
            cnt.current=0;
            setValue("search");
            setImg({});
          }
        else( //componentDidUpdate
            setTimeout(()=>{
            fetch("/members").then(
                res=>res.json()
            ).then(
                data=>{   
                    setData(data);
                    setReadystate("ready");
                    setCheck("ready");
                    console.log(data);
                }
            ).catch(err => {
                console.log(err);
                cnt.current++;
                setCheck("unready"+cnt.current);
                console.log(check);
            });},1000)

            
        )
    },[readystate,check, img]
)

    
    //async useEffect function
    /**useEffect(()=>{
        const fetchData = async () => {
            const data = await fetch('/members');  
            const json = await data.json();
            setData(json);
            setReadystate("ready");
        }
        fetchData().catch(console.error);}
        ,[readystate,data])**/

    return (
        <>
            <div className="navbar bg-dark navbar-dark justify-content-center align-items-center d-flex">
                <div>
                    <a  href="/#">
                        <img className="mb-1 me-3 d-flex d-inline-flex" src={require("../pic/home-button.png")} 
                        alt="Home" width="30" height="30" type="button"/>
                    </a>
                    
                    <li className="d-flex nav-item me-5 d-inline-flex">
                        <a className="nav-link text-white" href="/#">To Home</a>
                    </li>

                </div>
                <div className="d-flex flex-row  justify-content-center align-items-center">
                    <img className="mb-1 ms-3 " src={require("../pic/search.png")} alt="search"
                     width="30" height="30" type="submit"/>
                    <form className="col d-inline-flex ms-4 mb-3 mt-3" role="search"
                          action="http://localhost:5000/search" method="post" >
                        <input className="form-control me-2 pb-2 d-flex " type="search" 
                               placeholder="search" aria-label="Search" name="searchdata" defaultValue={value} onChange={(e)=>{setValue(e.target.value)}}/>
                        <button className="btn btn-outline-success ms-3" type="submit">Search</button>
                    </form>
                </div>  
            </div>
            
            <div className=" container-fluid  justify-content-center align-items-center d-flex">
                <div className="alert alert-warning shadow-lg text-darkU bg-orange-300 mt-3 ">
                    {value}
                </div>
            </div>

            <div className="container-fluid  justify-content-center align-items-center d-flex">
                <div className="row alert alert-warning justify-content-center p-1 m-1 shadow">
                    {(readystate==="unready")?(
                        <p className=" p-1 m-1 ">Loding{cnt.current}......</p>
                    ):(
                    data.members?.map((member,i)=>(
                        <a className="col col-3 btn btn-meteor d-block m-3 rounded shadow" key={i}
                        href="https://www.youtube.com/watch?v=13Dv9zvH4ko">{member}</a>
                    ))
                    )}
                </div>

            </div>

            <div className="d-flex">
                <div className="d-flex justify-content-center align-items-center">
                    <label className="d-flex mt-3">
                        <form  ref={formRef} className="btn btn-primary shadow-lg">
                            <input
                                type="file"
                                accept="image/*"
                                style={{ display: "none" }}
                                onChange={handleOnChange}
                            />
                            上傳檔案
                        </form>
                    </label>
                    <button className="btn btn-primary d-flex mt-3 ms-3 shadow-lg" onClick={handleOnResetForm}>Reset</button> 
                </div>
                
                <div className="d-flex flex-column justify-content-center align-items-center
                                mt-3">
                    {(imgready==="unready")?
                    <p></p>:
                    <div className="d-flex alert alert-meteor shadow p-1  flex-column justify-content-center align-items-center">
                        <p className="mt-2">File: {img.name}</p>
                        <p>Date: {img.date}</p>
                        <p>Type: {img.type}</p>
                        <img className="mb-3" style={{height:"0%", width:"50%  "}} src={imagesrc} alt="" />
                    </div>
                    }
                </div>
            </div>
        </>
    )   
}

export default Search;

/**{(typeof data.members==="undefined")?(
    <p className="bg-success">Loding......</p>
):(
data.members.map((member,i)=>(
    <a className="col col-3 btn btn-primary d-block m-3 " key={i}
    href="https://www.youtube.com/watch?v=13Dv9zvH4ko">{member}</a>
))
)}**/
