import React, {useState, useEffect, useRef} from 'react'

//this is customize bootstrap
import '../scss/all.css';

import '../jquery.js';
import '../bootstrap-5.2.1/dist/js/bootstrap.min.js';


const Login=()=>{
    return(
        <div className="d-flex justify-content-center align-items-center">
                <form  className="alert alert-secondary " action=" http://localhost:8080/api/login" method="post">
                    <span class="input-group-text">username</span>
                    <input className="input-group ms-3"type="text" name="username" placeholder="Enter Username" required/>

                    <span class="input-group-text">password</span>
                    <input className="input-group ms-3" type="password" name="password" placeholder="Enter Password" required/><br/>

                    <button type="submit">Login</button>
                </form>
        </div>
    )
}

export default Login;
