import React, {useState, useEffect, useRef} from 'react'

//this bootstrap is prototype
//import '../bootstrap-5.2.1/dist/css/bootstrap.min.css';

//this is customize bootstrap
import '../scss/all.css';

import '../jquery.js';
import '../bootstrap-5.2.1/dist/js/bootstrap.min.js';

function Search(props){
    const [data,setData]=useState([{}]);
    const [readystate,setReadystate]=useState("");
    const mounted=useRef();
    const [check,setCheck]=useState();
    const [value,setValue]=useState();
    let cnt=useRef();

    const handleOnChange = (event) => {
        console.log('files: ', event.target.files);
        console.log('type: ', event.target.files);
    };

    useEffect(()=>{
        if(!mounted.current){ //componentDidMount
            setReadystate("unready");
            mounted.current=true;
            cnt.current=0;
            setValue("search");
          }
        else( //componentDidUpdate
            setTimeout(()=>{
            fetch("/members").then(
                res=>res.json()
            ).then(
                data=>{   
                    setData(data);
                    setReadystate("ready");
                    setCheck("ready");
                    console.log(data);
                }
            ).catch(err => {
                console.log(err);
                cnt.current++;
                setCheck("unready"+cnt.current);
                console.log(check);
            })},1000)
        )
    },[readystate,check])

    
    //async useEffect function
    /**useEffect(()=>{
        const fetchData = async () => {
            const data = await fetch('/members');  
            const json = await data.json();
            setData(json);
            setReadystate("ready");
        }
        fetchData().catch(console.error);}
        ,[readystate,data])**/

    return (
        <>
            <div className="navbar bg-dark navbar-dark justify-content-center align-items-center d-flex">
                <div>
                    <a  href="/#">
                        <img className="mb-1 me-3 d-flex d-inline-flex" src={require("../pic/home-button.png")} 
                        alt="Home" width="30" height="30" type="button"/>
                    </a>
                    
                    <li className="d-flex nav-item me-5 d-inline-flex">
                        <a className="nav-link text-white" href="/#">To Home</a>
                    </li>

                </div>
                <div className="d-flex flex-row  justify-content-center align-items-center">
                    <img className="mb-1 ms-3 " src={require("../pic/search.png")} alt="search"
                     width="30" height="30" type="submit"/>
                    <form className="col d-inline-flex ms-4 mb-3 mt-3" role="search"
                          action="http://localhost:5000/search" method="post" >
                        <input className="form-control me-2 pb-2 d-flex " type="search" 
                               placeholder="search" aria-label="Search" name="searchdata" defaultValue={value} onChange={(e)=>{setValue(e.target.value)}}/>
                        <button className="btn btn-outline-success ms-3" type="submit">Search</button>
                    </form>
                </div>  
            </div>
            
            <div className=" container-fluid  justify-content-center align-items-center d-flex">
                <div className="alert alert-warning shadow-lg text-darkU bg-orange-300 mt-3 ">
                    {value}
                </div>
            </div>

            <div className="container-fluid  justify-content-center align-items-center d-flex">
                <div className="row alert alert-warning justify-content-center p-1 m-1 shadow">
                    {(readystate==="unready")?(
                        <p className=" p-1 m-1 ">Loding{cnt.current}......</p>
                    ):(
                    data.members?.map((member,i)=>(
                        <a className="col col-3 btn btn-meteor d-block m-3 rounded shadow" key={i}
                        href="https://www.youtube.com/watch?v=13Dv9zvH4ko">{member}</a>
                    ))
                    )}
                </div>
            </div>

            <div className="d-flex d-inline mt-4 justify-content-center align-items-center">
                <form>
                <button className="btn btn-primary">
                    <input
                        type="file"
                        accept="image/*"
                        component="label"
                        style={{ display: "none" }}
                        onChange={handleOnChange}
                    />
                    新增附件
                    </button>
                </form>
            </div>
        </>
    )   
}

export default Search;

