import React, {useState, useEffect, useRef} from 'react'
import {Link} from "react-router-dom";
//this is customize bootstrap
import '../scss/all.css';

import '../jquery.js';
import '../bootstrap-5.2.1/dist/js/bootstrap.min.js';
import '../bootstrap-5.2.1/dist/css/bootstrap.min.css';

const Login=()=>{
    return(
        <>
            <div className="d-flex justify-content-center">
                <div className="d-flex mt-3">
                    <form  className="alert alert-warning" action=" http://localhost:8080/api/login" method="post">
                        <div className="input-group mb-3">
                            <span className="input-group-text bg-dark">username</span>
                            <input type="text" className="form-control" name="username" placeholder="Username"/>
                        </div>
                        <div className="input-group mb-3">
                            <span className="input-group-text">password</span>
                            <input type="password" className="form-control" name="password" placeholder="passworld"/>
                        </div>
                        <div className="d-flex justify-content-center">
                            <button className="d-flex btn btn-outline-secondary" type="submit">Login</button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    )
}

export default Login;
